+public class Solution {
+
+  public String toLowerCase(final String str) {
+    final char[] myStr = str.toCharArray();
+    for (int i = 0; i < str.length(); i++) {
+      if (str.charAt(i) > 64 && str.charAt(i) < 92) {
+        myStr[i] += 32;
+      }
+    }
+    return String.valueOf(myStr);
+  }
