+public class Solution {
+
+  public void sortColors(final int[] nums) {
+    int zero = 0;
+    int one = 0;
+    for (int i = 0; i < nums.length; ++i) {
+      final int current = nums[i];
+      nums[i] = 2;
+      if (current < 2) {
+        nums[one++] = 1;
+      }
+      if (current == 0) {
+        nums[zero++] = 0;
+      }
+    }
+  }
