+import org.genxdm.xpath.v10.extend.ConvertibleExpr;
+
+public final class LocalNameFunction extends FunctionOpt1
+{
+    ConvertibleExprImpl makeCallExpr(final ConvertibleExpr expr, final StaticContext statEnv)
+        throws ExprParseException
+    {
+        final NodeSetExpr nse = expr.makeNodeSetExpr(statEnv);
+        return new ConvertibleStringExpr()
+        {
+
+            @Override
+            public <N> String stringFunction(Model<N> model, final N contextNode, final NodeDynamicContext<N> dynEnv)
+            {
+                final N node = nse.nodeIterator(model, contextNode, dynEnv).next();
+                if (node != null)
+                {
+                    final String localName = model.getLocalName(node);
+                    if (localName != null)
+                    {
+                        return localName;
+                    }
+                }
+                return "";
+            }
+
+            @Override
+            public String stringFunction(TraversingInformer contextNode, TraverserDynamicContext dynEnv)
+            {
+                Traverser node = nse.traverseNodes(contextNode, dynEnv);
+                if (node.moveToNext())
+                {
+                    final String localName = node.getLocalName();
+                    if (localName != null)
+                    {
+                        return localName;
+                    }
+                }
+                return "";
+            }
+        };
+    }
+
+}
