+      List<TypeString> transContentBatch =
+        transDoc.getContents().subList(index, bound);
+
+      List<TransUnitUpdateRequest> updateRequests = Lists.newArrayList();
+      index += bound;
+      for (int i = 0; i < batch.size(); i++) {
